import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import MetricsService, { LiveMetrics } from '../../services/MetricsService';
import EtherscanService from '../../utils/etherscan';

const PanelContainer = styled.div<{ isCollapsed: boolean }>`
  position: fixed;
  top: 20px;
  right: 20px;
  width: ${props => props.isCollapsed ? '60px' : '400px'};
  max-height: 80vh;
  background: rgba(10, 10, 15, 0.95);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(138, 112, 255, 0.3);
  border-radius: 16px;
  z-index: 1000;
  transition: all 0.3s ease;
  overflow: hidden;
`;

const PanelHeader = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 16px;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
  cursor: pointer;
`;

const Title = styled.h3`
  color: #ffffff;
  font-size: 16px;
  font-weight: 600;
  margin: 0;
`;

const CollapseButton = styled.button`
  background: none;
  border: none;
  color: #8a70ff;
  font-size: 18px;
  cursor: pointer;
  padding: 4px;
  border-radius: 4px;
  transition: background-color 0.2s ease;

  &:hover {
    background-color: rgba(138, 112, 255, 0.1);
  }
`;

const PanelContent = styled.div<{ isCollapsed: boolean }>`
  display: ${props => props.isCollapsed ? 'none' : 'block'};
  padding: 16px;
  max-height: calc(80vh - 60px);
  overflow-y: auto;
`;

const MetricsGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 12px;
  margin-bottom: 20px;
`;

const MetricCard = styled.div`
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 8px;
  padding: 12px;
`;

const MetricLabel = styled.div`
  color: #888;
  font-size: 12px;
  margin-bottom: 4px;
`;

const MetricValue = styled.div<{ color?: string }>`
  color: ${props => props.color || '#ffffff'};
  font-size: 16px;
  font-weight: 600;
`;

const Section = styled.div`
  margin-bottom: 20px;
`;

const SectionTitle = styled.h4`
  color: #8a70ff;
  font-size: 14px;
  font-weight: 600;
  margin: 0 0 12px 0;
  text-transform: uppercase;
