import React, { useEffect, useState } from 'react';
import styled from 'styled-components';

const BackgroundContainer = styled.div<{ loaded: boolean; fixed?: boolean }>`
  position: ${props => props.fixed ? 'fixed' : 'absolute'};
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  z-index: 0;
  opacity: ${props => props.loaded ? 1 : 0};
  transition: opacity 0.5s ease;
`;

const Image = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
  object-position: center;
`;

const Overlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(14, 14, 28, 0.8);
  z-index: 1;
`;

// Add a subtle noise texture overlay
const NoiseOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-image: url('data:image/png;base64,');
  opacity: 0.02;
  z-index: 2;
  pointer-events: none;
`;

interface BackgroundImageProps {
  src: string;
  className?: string;
  overlayOpacity?: number;
  fixed?: boolean;
}

const BackgroundImage: React.FC<BackgroundImageProps> = ({ 
  src, 
  className,
  overlayOpacity = 0.8,
  fixed = true
}) => {
  const [loaded, setLoaded] = useState(false);

  useEffect(() => {
    const img = document.createElement('img');
    img.src = src;
    img.onload = () => setLoaded(true);
  }, [src]);

  return (
    <BackgroundContainer className={className} loaded={loaded} fixed={fixed}>
      <Image src={src} alt="Background" />
      <Overlay style={{ backgroundColor: `rgba(14, 14, 28, ${overlayOpacity})` }} />
      <NoiseOverlay />
    </BackgroundContainer>
  );
};

export default BackgroundImage;
